SELECT `packages`.`label`,`packages`.`versionLabel`,`device_profiles`.`uuid`,`devices`.`version`,COUNT(`device_profiles`.`package`) FROM `packages`,`devices`,`device_profiles`,`permissions` WHERE (`devices`.`version` > "4.1" AND `devices`.`uuid` = `device_profiles`.`uuid` AND `device_profiles`.`package` = `packages`.`checksum` AND `permissions`.`name` = `device_profiles`.`permission` AND `permissions`.`protectionLevel` = 1) GROUP BY `device_profiles`.`package`;